// src/pages/ProfilePage.jsx
import { useState, useEffect } from "react";
import axios from "axios";

function ProfilePage() {
  const [profile, setProfile] = useState({});
  const [updateName, setUpdateName] = useState("");
  const [updateNickname, setUpdateNickname] = useState("");
  const [updateEmail, setUpdateEmail] = useState("");

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        window.location.href = "/login";
        return;
      }

      const response = await axios.get("http://localhost:8080/api/user/profile", {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setProfile(response.data);
    } catch (error) {
      console.error("Error fetching profile:", error);
      // If token invalid/expired, redirect to login
      window.location.href = "/login";
    }
  };

  const handleUpdateProfile = async () => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:8080/api/user/profile/update",
        {
          name: updateName || undefined,
          nickname: updateNickname || undefined,
          email: updateEmail || undefined,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      alert("Profile updated!");
      fetchProfile(); // re-fetch updated info
    } catch (error) {
      console.error("Error updating profile:", error);
      alert("Update failed.");
    }
  };

  return (
    <div>
      <h2>Profile Page</h2>
      <p><strong>Name:</strong> {profile.name}</p>
      <p><strong>Nickname:</strong> {profile.nickname}</p>
      <p><strong>Email:</strong> {profile.email}</p>

      <h3>Update Profile</h3>
      <input
        type="text"
        placeholder="New name"
        onChange={(e) => setUpdateName(e.target.value)}
      />
      <br />
      <input
        type="text"
        placeholder="New nickname"
        onChange={(e) => setUpdateNickname(e.target.value)}
      />
      <br />
      <input
        type="email"
        placeholder="New email"
        onChange={(e) => setUpdateEmail(e.target.value)}
      />
      <br />
      <button onClick={handleUpdateProfile}>Save Changes</button>
    </div>
  );
}

export default ProfilePage;
